#!/bin/Bash
# set -x

echo "unit and other tests for Q3Tax program:"


ls -l Q3*



echo "Smoke test. do one thing once. expect something like: Necessity item;  price: 100  tax: 1  = Total: 101"
java Q3Tax n 100

echo ""


echo "Unit test. Try each of the mandated L,N,l,n, integer arguments. Make changes 1 at a time, but put combined tests first to fail quickly after release."

echo "expect: Luxury item;  price: 100  tax: 9  = Total: 109"
java Q3Tax l 100

echo "tax: 1"
java Q3Tax N 100

echo "tax: 90000"
java Q3Tax L 1000000

echo "tax: 10000 "
java Q3Tax N 1000000

echo "tax: 5994  = Total: 72589"
java Q3Tax l 66595

echo "expect tax: 111111110  = Total: 1345679001"
java Q3Tax l 1234567891


echo ""


echo "Smoke test. do one thing once. expect something like: Necessity item;  price: 100  tax: 1  = Total: 101"
java Q3Tax n 100


echo "values too large:"

java Q3Tax n -1234567891

java Q3Tax l 2034567891

java Q3Tax l -2034567891



echo "not correct args, but numbers cause no error:"
echo "2 args: 100 100"
java Q3Tax 100 100

echo "2 args: 100 987654321"
java Q3Tax 100 987654321

echo "2 args: 1 1"
java Q3Tax 1 1


echo ""

echo "What does 'a' mean? What should it mean? Z? 7? &? ?? :? etc."
java Q3Tax a 100
echo "What does 'A' mean?"
java Q3Tax A 100

echo "What does 'Z' mean?"
java Q3Tax Z 100
echo "What does 'z' mean?"
java Q3Tax z 100

echo "What does '7' mean?"
java Q3Tax 7 100
echo "What does '&' mean?"
java Q3Tax & 100

echo "What does '?' mean?"
java Q3Tax ? 100
echo "What does ':' mean?"
java Q3Tax : 100

echo "What does 'abcdefghijklmnopqrstuvwxyz' mean?"
java Q3Tax abcdefghijklmnopqrstuvwxyz  100




echo "'NN' tax: 1"
java Q3Tax NN 100

echo "'LLLLLLLLLLLL' tax: 90000"
java Q3Tax LLLLLLLLLLLLLLLL 1000000

echo "'NNN..1000x..NN' tax: 1"
java Q3Tax  100


echo "'NNN..3000x..NN' tax: 1"
java Q3Tax  100


echo "tax: 5994  = Total: 72589"
java Q3Tax l 66595

echo "expect tax: 111111110  = Total: 1345679001"
java Q3Tax l 1234567891



echo "'L 000..00091' tax: 8"
java Q3Tax L 

echo "'L 000..1000x...00091' tax: 8"
java Q3Tax L 

echo "'NNNN..3000..NNN 000..1000x...00091' tax: 8"
java Q3Tax  


echo "multiple categories and prices in a single command line are processed without error"

java Q3Tax L 100  N 100 l 200 n 200 a 400 B 400 500 500 665.95 700  L 100


echo "no args: no output!"
java Q3Tax 

echo ""


echo "Smoke test. do one thing once. expect something like: Necessity item;  price: 100  tax: 1  = Total: 101"
java Q3Tax n 100


echo "one arg: N: no output!"
java Q3Tax N 

echo "one arg: L"
java Q3Tax L

echo "one arg: n"
java Q3Tax n 

echo "one arg: l"
java Q3Tax l

echo "one arg: 23456789"
java Q3Tax 23456789

echo "one arg: -23456789"
java Q3Tax -23456789

echo "one arg: -20.3456.."
java Q3Tax -20.34567891

echo "one arg: 100"
java Q3Tax 100

echo ""
echo ""

echo "These are expected to fail!"

echo "incorrectly formed ints:"

echo ""
java Q3Tax n 100.00e2

java Q3Tax n 100.00e-3

java Q3Tax n 0x1

java Q3Tax n 0xFF

echo ""

echo "not correct args:"
java Q3Tax :!@#$%^&*_+|:<>? 100
java Q3Tax l l
java Q3Tax N N 
java Q3Tax n n
java Q3Tax L L
java Q3Tax a A
java Q3Tax Z z 
java Q3Tax ? ?
java Q3Tax @ #

echo ""


echo "values too small:"

java Q3Tax N .2034567891

java Q3Tax N 0.2034567891

java Q3Tax N -20.34567891

java Q3Tax L .00000000000000000002034567891

java Q3Tax L 0.00000000000000000002034567891

java Q3Tax l -20.3400000000000000000000567891


echo "done!"




